hibernate-mapping.sql-query:
DECLARE @TIPO INT BEGIN SET @TIPO = ( select isnull(tipodia.tipo, 0) from ( select case :inicio -->parametro when 1 then lunes when 2 then martes when 3 then miercoles when 4 then jueves when 5 then viernes when 6 then sabado when 7 then domingo else 0 end as tipo from empleadomast a join as_TipoHorario b on b.tipohorario = a.tipohorario where a.empleado = :persona and a.companiasocio = :compania) tipodia)
select 'De ' + CONVERT(VARCHAR(5),HoraInicio.Hora,108)+' a '+CONVERT(VARCHAR(5), HoraFin.Hora,108) as horario from As_TipoDiaAsistencia join
( select min ( horainicio ) As Hora , Tipodia AS TipoDia from as_tipodiamovimiento join ( select min ( secuencia ) Secuencia from as_tipodiamovimiento where tipodia = @TIPO and IntervaloAcceso ='TRAB' ) Secuencia on Secuencia.Secuencia =as_tipodiamovimiento.Secuencia where Tipodia = @TIPO and IntervaloAcceso ='TRAB' group by Tipodia
) HoraInicio on HoraInicio.TipoDia = As_TipoDiaAsistencia.TipoDia join ( select max ( HoraFin ) As Hora , Tipodia AS TipoDia from as_tipodiamovimiento join ( select max ( secuencia ) Secuencia from as_tipodiamovimiento where tipodia = @TIPO and IntervaloAcceso ='TRAB' ) Secuencia on Secuencia.Secuencia = as_tipodiamovimiento.Secuencia where tipodia = @TIPO and IntervaloAcceso ='TRAB' group by Tipodia
) HoraFin on HoraFin.TipoDia =As_TipoDiaAsistencia.TipoDia
where As_TipoDiaAsistencia.TipoDia = @TIPO END

DECLARE @TIPO INT BEGIN SET @TIPO = ( select isnull(tipodia.tipo, 0) from ( select case :inicio -->parametro when 1 then lunes when 2 then martes when 3 then miercoles when 4 then jueves when 5 then viernes when 6 then sabado when 7 then domingo else 0 end as tipo from empleadomast a join as_TipoHorario b on b.tipohorario = a.tipohorario where a.empleado = :persona and a.companiasocio = :compania) tipodia)
select HoraFin.Hora as horario from As_TipoDiaAsistencia join
( select min ( horainicio ) As Hora , Tipodia AS TipoDia from as_tipodiamovimiento join ( select min ( secuencia ) Secuencia from as_tipodiamovimiento where tipodia = @TIPO and (IntervaloAcceso ='TRAB' or 'S' = :conSobretiempo ) ) Secuencia on Secuencia.Secuencia =as_tipodiamovimiento.Secuencia where Tipodia = @TIPO and (IntervaloAcceso ='TRAB' or 'S' = :conSobretiempo ) group by Tipodia
) HoraInicio on HoraInicio.TipoDia = As_TipoDiaAsistencia.TipoDia join ( select max ( HoraFin ) As Hora , Tipodia AS TipoDia from as_tipodiamovimiento join ( select max ( secuencia ) Secuencia from as_tipodiamovimiento where tipodia = @TIPO and (IntervaloAcceso ='TRAB' or 'S' = :conSobretiempo ) ) Secuencia on Secuencia.Secuencia = as_tipodiamovimiento.Secuencia where tipodia = @TIPO and (IntervaloAcceso ='TRAB' or 'S' = :conSobretiempo ) group by Tipodia
) HoraFin on HoraFin.TipoDia =As_TipoDiaAsistencia.TipoDia
where As_TipoDiaAsistencia.TipoDia = @TIPO END

DECLARE @TIPO INT BEGIN SET @TIPO = ( select isnull(tipodia.tipo, 0) from ( select case :inicio -->parametro when 1 then lunes when 2 then martes when 3 then miercoles when 4 then jueves when 5 then viernes when 6 then sabado when 7 then domingo else 0 end as tipo from empleadomast a join as_TipoHorario b on b.tipohorario = a.tipohorario where a.empleado = :persona and a.companiasocio = :compania) tipodia)
select HoraInicio.Hora as horario from As_TipoDiaAsistencia join
( select min ( horainicio ) As Hora , Tipodia AS TipoDia from as_tipodiamovimiento join ( select min ( secuencia ) Secuencia from as_tipodiamovimiento where tipodia = @TIPO and (IntervaloAcceso ='TRAB' or 'S' = :conSobretiempo ) ) Secuencia on Secuencia.Secuencia =as_tipodiamovimiento.Secuencia where Tipodia = @TIPO and (IntervaloAcceso ='TRAB' or 'S' = :conSobretiempo ) group by Tipodia
) HoraInicio on HoraInicio.TipoDia = As_TipoDiaAsistencia.TipoDia join ( select max ( HoraFin ) As Hora , Tipodia AS TipoDia from as_tipodiamovimiento join ( select max ( secuencia ) Secuencia from as_tipodiamovimiento where tipodia = @TIPO and (IntervaloAcceso ='TRAB' or 'S' = :conSobretiempo ) ) Secuencia on Secuencia.Secuencia = as_tipodiamovimiento.Secuencia where tipodia = @TIPO and (IntervaloAcceso ='TRAB' or 'S' = :conSobretiempo ) group by Tipodia
) HoraFin on HoraFin.TipoDia =As_TipoDiaAsistencia.TipoDia
where As_TipoDiaAsistencia.TipoDia = @TIPO END
